package demoone;

public class loopconcept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;
				while(i<=10) {
					System.out.println(i);
					i=i+1;
}
				System.out.println("*************");
				
				for(int j=1;j<=10;j++) {
					System.out.println(j);
				}
				System.out.println("*************");
				
				for(int k=10;k>=-20;k--) {
					System.out.println(k);
				}
	}

}
